								double dr[3] = { at[xa].x - wrap_to[0], at[xa].y - wrap_to[1], at[xa].z - wrap_to[2] };
								wrapPBC( dr, rsurf+3*nv );
		
								at[xa].x = wrap_to[0] + dr[0];
								at[xa].y = wrap_to[1] + dr[1];
								at[xa].z = wrap_to[2] + dr[2];
