							{
								wrap_to[0] = at[xa].x;
								wrap_to[1] = at[xa].y;
								wrap_to[2] = at[xa].z;
							}
		
	
		
							at[xa].bead = cur_atom;
							at[xa].res  = cur_res;
							at[xa].segRes = cur_res;
	
							char temp_segid[256];
							sprintf(temp_segid, "MEMB");
